import React from 'react';
import Box from '@material-ui/core/Box';
import DataCard from './Components/DataCard';



const serverAddress = 'http://192.168.1.220:3000';
const styles = {

}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: "N/A",
      humidity: "N/A",
      moisture: "N/A"
    }

    this.updateData = this.updateData.bind(this);
  }

  // Accepts the type of data, which can only be 'temperature', 'humidity', or 'moisture' (the current
  // api 'get' endpoints)
  async getData(type) {
    if (type === 'temperature' ||
        type === 'humidity' ||
        type === 'moisture') {
      try {
        let rawData = await fetch(serverAddress + '/' + type);
        let data = rawData.json();
        return data;
      } catch (err) {
        return err;
      }
    } else {
      return 'Invalid request type';
    }
  }

  updateData(type) {
    fetch(serverAddress + '/' + type)
      .then(rawData => rawData.json())
      .then(data => this.setState({ [type]: data[type] }));
  }


  render() {
    return (
      <React.Fragment>
        <Box margin="30%" display="flex" justifyContent="space-evenly">
          <DataCard name={'Temperature'} value={this.state.temperature} onClick={() => this.updateData('temperature')} />
          <DataCard name={'Humidity'} value={this.state.humidity} onClick={() => this.updateData('humidity')} />
          <DataCard name={'Moisture'} value={this.state.moisture} onClick={() => this.updateData('moisture')} />
        </Box>
      </React.Fragment>
     );
  }

}

export default App;
