import React from 'react';
import DataCard from './Components/DataCard';



const serverAddress = '192.168.1.220:3000';
const styles = {

}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: 0,
      humidity: 0,
      moisture: 0
    }

    this.updateData = this.updateData.bind(this);
  }

  // Accepts the type of data, which can only be 'temperature', 'humidity', or 'moisture' (the current
  // api 'get' endpoints)
  async getData(type) {
    if (type === 'temperature' ||
        type === 'humidity' ||
        type === 'moisture') {
      try {
        let data = await fetch(serverAddress + '/' + type);
        return data;
      } catch {
        return 'Error getting data';
      }
    } else {
      return 'Invalid request type';
    }
  }

  async updateData(type) {
    let rawData = await this.getData(type);
    this.setState({ temperature: rawData[type] });
    console.log(rawData);
  }


  render() {
    return (
      <React.Fragment>
        <DataCard name={'Temperature'} value={this.state.temperature} onClick={() => this.updateData('temperature')}/>
        <DataCard name={'Humidity'} value={this.state.humidity}/>
        <DataCard name={'Moisture'} value={this.state.moisture}/>
      </React.Fragment>
     );
  }

}

export default App;
