import React from 'react';
import { styled } from '@material-ui/core/styles';
import { ResponsiveContainer, LineChart, Line, XAxis, YAxis, Tooltip, Legend } from 'recharts';
import { Typography, TextField } from '@material-ui/core';



const UnitExplanationText = styled(Typography) ({
  textAlign: 'center',
  fontStyle: 'italic',
  color: '#bfbfbf',
})

const NumRecordsTextField = styled(TextField) ({
  margin: '0 auto'
})


// Accepts the data as an array, with the format: 
// [ {date, temperature, humidity, moisture}, {date: val, temperature: val, humidty: val, moisture: val} ]
function HistoricalDataChart({ data }) {
  return(
    <React.Fragment>

      <div style={{paddingLeft: '5%'}}>
      <ResponsiveContainer width="90%" height={500}>
        <LineChart data={data}>
          <XAxis dataKey="date"/>
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="temperature" stroke="#ee0000"/>
          <Line type="monotone" dataKey="humidity" stroke="#00ee00"/>
          <Line type="monotone" dataKey="moisture" stroke="#0000ee"/>
        </LineChart>
      </ResponsiveContainer>
      </div>

      <UnitExplanationText>
        The temperature is in degrees fahrenheit while the humidity and moisture values are percentages.
      </UnitExplanationText>

      <NumRecordsTextField label="Number of Records:"/> 


    </React.Fragment>
  )
}

export default HistoricalDataChart;
