import React from 'react';
import Grid from '@material-ui/core/Grid';
import DataCard from './Components/DataCard';
import HistoricalDataChart from './Components/HistoricalDataChart';
import { styled } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';


// Address of my raspberry pi, running an express server on port 3000
const serverAddress = 'http://192.168.1.220:3000';


const Title = styled(Typography) ({
  textAlign: "center",
  fontSize: "50px",
})


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: "N/A",
      humidity: "N/A",
      moisture: "N/A",
      historicalData: []
    }

    this.updateData = this.updateData.bind(this);
  }

  componentDidMount() {
    this.updateData('temperature');
    this.updateData('humidity');
    this.updateData('moisture');
  }

  // Accepts the type of data, which can only be 'temperature', 'humidity', or 'moisture' (the current
  // api 'get' endpoints)
  async getData(type) {
    if (type === 'temperature' ||
        type === 'humidity' ||
        type === 'moisture') {
      try {
        let rawData = await fetch(serverAddress + '/' + type);
        let data = rawData.json();
        return data;
      } catch (err) {
        return err;
      }
    } else {
      return 'Invalid request type';
    }
  }

  updateData(type) {
    fetch(serverAddress + '/' + type)
      .then(rawData => rawData.json())
      .then(data => this.setState({ [type]: data[type] }));
  }

  // Gets the historical data for the sensor values
  async getHistoricalData() {
    fetch(serverAddress + '/historical-data')
      .then(rawData => rawData.json())
      .then(data => this.setState({ historicalData: data}))
  }

  render() {
    return ( 
      <React.Fragment>

        <Title>
          Dashboard
        </Title>

        <Grid container margin='100px' direction="row" justify="space-evenly" spacing={5} >
          <Grid item>
            <DataCard name={'Temperature'} value={this.state.temperature} onClick={() => this.updateData('temperature')} />
          </Grid> 

          <Grid item>
            <DataCard name={'Humidity'} value={this.state.humidity} onClick={() => this.updateData('humidity')} />
          </Grid>

          <Grid item>
            <DataCard name={'Moisture'} value={this.state.moisture} onClick={() => this.updateData('moisture')} />
          </Grid>
        </Grid>

        <HistoricalDataChart data={this.state.historicalData}/>

      </React.Fragment>
     );
  }

}

export default App;
