import React from 'react';
import DataCard from './Components/DataCard';
import Button from '@material-ui/core/Button';



const serverAddress = '192.168.1.220:3000';
const styles = {

}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: 0,
      humidity: 0,
      moisture: 0
    }
  }

  // Accepts the type of data, which can only be 'temperature', 'humidity', or 'moisture' (the current
  // api 'get' endpoints)
  async getData(type) {
    if (type === 'temperature' ||
        type === 'humidity' ||
        type === 'moisture') {
      try {
        let data = await fetch(serverAddress + '/' + type);
        return data;
      } catch {
        return 'Error getting data';
      }
    } else {
      throw 'Invalid request type';
    }
  }


  render() {
    return (
      <React.Fragment>
        <DataCard name={'Temperature'} value={this.state.temperature}/>
        <DataCard name={'Humidity'} value={this.state.humidity}/>
        <DataCard name={'Moisture'} value={this.state.moisture}/>
      </React.Fragment>
     );
  }

}

export default App;
