import React from 'react';
import { styled } from '@material-ui/core/styles';
import { ResponsiveContainer, LineChart, Line, XAxis, YAxis, Tooltip, Legend } from 'recharts';
import { Typography } from '@material-ui/core';


const UnitExplanationText = styled(Typography) ({
  textAlign: 'center',

})


// Accepts the data as an array, with the format: 
// [ {date, temperature, humidity, moisture}, {date: val, temperature: val, humidty: val, moisture: val} ]
function HistoricalDataChart({ data }) {
  return(
    <React.Fragment>

      <ResponsiveContainer width="90%" height={500} margin={{top: 5, right: '5%', left: '5%', bottom: 5}}>
        <LineChart data={data} >
          <XAxis dataKey="date"/>
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="temperature" stroke="#ee0000"/>
          <Line type="monotone" dataKey="humidity" stroke="#00ee00"/>
          <Line type="monotone" dataKey="moisture" stroke="#0000ee"/>
        </LineChart>
      </ResponsiveContainer>

      <UnitExplanationText>
        The temperature is in degrees farenheit while the humidity and moisture values are percentages.
      </UnitExplanationText>

    </React.Fragment>
  )
}

export default HistoricalDataChart;
